mapper.select:
select *
from auth_branch

    and status = 1
    
        and id = #{id,jdbcType=BIGINT}
    
    
        and code = #{code,jdbcType=VARCHAR}
    
    
        and name = #{name,jdbcType=VARCHAR}
    
    
        and name_spell = #{nameSpell,jdbcType=VARCHAR}
    
    
        and status_id = #{statusId,jdbcType=BIT}
    
    
        and area = #{area,jdbcType=VARCHAR}
    
    
        and warehouse_code = #{warehouseCode,jdbcType=VARCHAR}
    
    
        and area_code = #{areaCode,jdbcType=VARCHAR}
    
    
        and baud_rate = #{baudRate,jdbcType=INTEGER}
    
    
        and is_belong = #{isBelong,jdbcType=VARCHAR}
    
    
        and cainiao_warehouse_code = #{cainiaoWarehouseCode,jdbcType=VARCHAR}
    
    
        and abbreviation_name = #{abbreviationName,jdbcType=VARCHAR}
    
    
        and settle = #{settle,jdbcType=BIT}
